const hoje = new Date();
const amanha = new Date(hoje);
amanha.setDate(hoje.getUTCDate() + 1);

let diaAmanha = "";
let mesAmanha = "";
let anoAmanha = amanha.getUTCFullYear();

diaAmanha = amanha.getUTCDate() >= 0 && amanha.getUTCDate() <= 9 ? `0${amanha.getUTCDate()}` : amanha.getUTCDate();
mesAmanha = amanha.getUTCMonth() >= 0 && amanha.getUTCMonth() <= 9 ? `0${amanha.getUTCMonth() + 1}` : amanha.getUTCMonth() + 1;

const previsaoTermino = `${anoAmanha}${mesAmanha}${diaAmanha}`;

msg.dataTermino = previsaoTermino;

return msg;
